import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { AuthGuard } from './core/auth/auth.guard';
import { HomeComponent } from './features/home/home.component';
import { CursosComponent } from './features/cursos/cursos.component';
import { SemestresComponent } from './features/semestres/semestres.component';
import { DisciplinasComponent } from './features/disciplinas/disciplinas.component';
import { AcessoNegadoComponent } from './shared/acesso-negado/acesso-negado.component';

const routes: Routes = [
  { path: '', component: HomeComponent, canActivate: [AuthGuard] },

  // Coordenador (CRUDs)
  {
    path: 'cursos',
    component: CursosComponent,
    canActivate: [AuthGuard],
    data: { roles: ['COORDENADOR'] },
  },
  {
    path: 'semestres',
    component: SemestresComponent,
    canActivate: [AuthGuard],
    data: { roles: ['COORDENADOR'] },
  },
  {
    path: 'disciplinas',
    component: DisciplinasComponent,
    canActivate: [AuthGuard],
    data: { roles: ['COORDENADOR'] },
  },

  // Admin (matrículas, gestão de professores)
  {
    path: 'admin',
    loadChildren: () =>
      import('./features/admin/admin.module').then((m) => m.AdminModule),
    canActivate: [AuthGuard],
    data: { roles: ['ADMIN'] },
  },

  // Página de acesso negado
  { path: 'acesso-negado', component: AcessoNegadoComponent },

  // Fallback
  { path: '**', redirectTo: '' },
];

@NgModule({
  imports: [RouterModule.forRoot(routes)],
  exports: [RouterModule],
})
export class AppRoutingModule {}
