import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { LayoutComponent } from './core/layout/layout.component';
import { AppAuthGuard } from './core/auth/app-auth.guard';

const routes: Routes = [
  {
    path: '',
    component: LayoutComponent,
    children: [
      // home livre (depois podemos mostrar um dashboard simples)
      { path: 'home', loadChildren: () => import('./features/home/home.module').then(m => m.HomeModule) },

      // matriz: acessível a ALUNO e PROFESSOR
      {
        path: 'matriz',
        canActivate: [AppAuthGuard],
        data: { roles: ['ALUNO', 'PROFESSOR'] },
        loadChildren: () => import('./features/matriz/matriz.module').then(m => m.MatrizModule),
      },

      // coordenador: CRUD cursos/semestres/disciplinas
      {
        path: 'coordenador',
        canActivate: [AppAuthGuard],
        data: { roles: ['COORDENADOR'] },
        loadChildren: () => import('./features/coordenador/coordenador.module').then(m => m.CoordenadorModule),
      },

      // admin: gerenciar professores/alunos e atribuir coordenador
      {
        path: 'admin',
        canActivate: [AppAuthGuard],
        data: { roles: ['ADMIN'] },
        loadChildren: () => import('./features/admin/admin.module').then(m => m.AdminModule),
      },

      { path: '', pathMatch: 'full', redirectTo: 'home' },
      { path: '**', redirectTo: 'home' },
    ],
  },
  { path: 'home', loadChildren: () => import('./features/home/home.module').then(m => m.HomeModule) },
];

@NgModule({
  imports: [RouterModule.forRoot(routes, { useHash: false })],
  exports: [RouterModule],
})
export class AppRoutingModule {}
